import UIKit

class MovieHubInteractor: MovieHubInteractorProtocol {
    
    func onSectionsFetch(sections: [CVMovieHubViewEntitySection]) {
        presenter?.onSectionsFetch(sections: sections)
    }
    
    
    
    func tryFetchSections() {
        CVMovieApiManager().getMovies { sections in
            self.presenter?.onSectionsFetch(sections: sections.configuration)
        }
    }
    
 
    

    weak var presenter: MovieHubPresenterProtocol?
}
